import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const Navbar = ({ account }) => {
  const formatAddress = (address) => {
    if (!address) return '';
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  };

  return (
    <motion.nav
      className="navbar"
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <div className="nav-content">
        <Link to="/" className="nav-logo">
          <img src="/logo192.png" alt="Logo" style={{ height: 32, marginRight: 12, verticalAlign: 'middle' }} />
          <span style={{ fontWeight: 700, fontSize: '1.25rem', verticalAlign: 'middle' }}>CharityChain</span>
        </Link>
        <div className="nav-links">
          <Link to="/campaigns">
            <motion.span
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Campaigns
            </motion.span>
          </Link>
          <Link to="/create">
            <motion.span
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Create Campaign
            </motion.span>
          </Link>
          {account && (
            <motion.div
              className="account-info"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.2 }}
              style={{ background: '#f0f4fa', color: '#4a90e2', padding: '0.5rem 1rem', borderRadius: 20, fontWeight: 500, marginLeft: 16 }}
            >
              <span style={{ marginRight: 6, fontSize: 14 }}>Wallet:</span> {formatAddress(account)}
            </motion.div>
          )}
        </div>
      </div>
    </motion.nav>
  );
};

export default Navbar;
