import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import { Routes, Route, Link } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import CharityPlatform from './contracts/CharityPlatform.json';
import Home from './components/Home';

// Update this address with the one from your deployment
const contractAddress = "0x5FbDB2315678afecb367f032d93F642f64180aa3";

function App() {
  const [account, setAccount] = useState('');
  const [contract, setContract] = useState(null);
  const [campaigns, setCampaigns] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [txStatus, setTxStatus] = useState('');
  const [networkStatus, setNetworkStatus] = useState('');

  useEffect(() => {
    const init = async () => {
      if (window.ethereum) {
        try {
          setLoading(true);
          setError('');
          setNetworkStatus('Connecting to network...');

          // Check if we're on the correct network
          const provider = new ethers.providers.Web3Provider(window.ethereum);
          const network = await provider.getNetwork();
          
          if (network.chainId !== 1337) {
            setError("Please connect to the local Hardhat network (Chain ID: 1337)");
            setLoading(false);
            return;
          }

          setNetworkStatus('Network connected. Requesting account access...');

          // Request account access
          const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
          setAccount(accounts[0]);

          setNetworkStatus('Account connected. Initializing contract...');

          // Create contract instance
          const signer = provider.getSigner();
          const contractInstance = new ethers.Contract(contractAddress, CharityPlatform.abi, signer);
          
          // Verify contract is deployed
          try {
            await contractInstance.campaignCount();
            setContract(contractInstance);
            setNetworkStatus('Contract initialized. Loading campaigns...');
            await loadCampaigns(contractInstance);
            setNetworkStatus('Ready');
          } catch (error) {
            console.error("Contract verification failed:", error);
            setError("Contract not deployed or wrong address. Please deploy the contract first.");
          }
        } catch (error) {
          console.error("Error initializing:", error);
          setError("Failed to connect to MetaMask. Please make sure MetaMask is installed and connected to the correct network.");
        }
      } else {
        setError("Please install MetaMask to use this application.");
      }
      setLoading(false);
    };

    init();
  }, []);

  const loadCampaigns = async (contractInstance) => {
    try {
      const campaignCount = await contractInstance.campaignCount();
      const loadedCampaigns = [];

      for (let i = 1; i <= campaignCount; i++) {
        const campaign = await contractInstance.getCampaign(i);
        loadedCampaigns.push({
          id: campaign.id.toString(),
          title: campaign.title,
          description: campaign.description,
          beneficiary: campaign.beneficiary,
          goal: ethers.utils.formatEther(campaign.goal),
          raised: ethers.utils.formatEther(campaign.raised),
          deadline: new Date(campaign.deadline.toNumber() * 1000).toLocaleDateString(),
          active: campaign.active
        });
      }

      setCampaigns(loadedCampaigns);
    } catch (error) {
      console.error("Error loading campaigns:", error);
      setError("Failed to load campaigns. Please make sure you're connected to the correct network.");
    }
  };

  const createCampaign = async (event) => {
    event.preventDefault();
    setError('');
    setTxStatus('Creating campaign...');
    
    try {
      const formData = new FormData(event.target);
      const title = formData.get('title');
      const description = formData.get('description');
      const beneficiary = formData.get('beneficiary');
      const goal = formData.get('goal');
      const duration = formData.get('duration');

      // Validate inputs
      if (!ethers.utils.isAddress(beneficiary)) {
        throw new Error('Invalid beneficiary address');
      }

      if (isNaN(goal) || parseFloat(goal) <= 0) {
        throw new Error('Goal amount must be greater than 0');
      }

      if (isNaN(duration) || parseInt(duration) <= 0) {
        throw new Error('Duration must be greater than 0');
      }

      // Check if contract is properly connected
      if (!contract) {
        throw new Error('Contract not connected. Please refresh the page.');
      }

      console.log('Creating campaign with:', {
        title,
        description,
        beneficiary,
        goal: ethers.utils.parseEther(goal),
        duration: parseInt(duration)
      });

      const tx = await contract.createCampaign(
        title,
        description,
        beneficiary,
        ethers.utils.parseEther(goal),
        parseInt(duration)
      );
      
      setTxStatus('Waiting for transaction confirmation...');
      const receipt = await tx.wait();
      console.log('Transaction receipt:', receipt);
      
      setTxStatus('Campaign created successfully!');
      await loadCampaigns(contract);
      event.target.reset();
    } catch (error) {
      console.error("Error creating campaign:", error);
      let errorMessage = "Failed to create campaign. ";
      
      if (error.code === 'INSUFFICIENT_FUNDS') {
        errorMessage += "You don't have enough ETH to cover the gas fees.";
      } else if (error.code === 'UNPREDICTABLE_GAS_LIMIT') {
        errorMessage += "Gas estimation failed. Please try again.";
      } else if (error.message) {
        errorMessage += error.message;
      }
      
      setError(errorMessage);
      setTxStatus('');
    }
  };

  const donate = async (campaignId, amount) => {
    try {
      const tx = await contract.donate(campaignId, {
        value: ethers.utils.parseEther(amount)
      });
      await tx.wait();
      await loadCampaigns(contract);
    } catch (error) {
      console.error("Error making donation:", error);
      setError("Failed to make donation. Please try again.");
    }
  };

  const formatAddress = (address) => {
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  };

  const formatDate = (timestamp) => {
    const date = new Date(timestamp * 1000);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const Navbar = () => (
    <motion.nav 
      className="navbar"
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <div className="nav-content">
        <Link to="/" className="nav-logo">Blockchain Charity</Link>
        <div className="nav-links">
          <Link to="/campaigns">Campaigns</Link>
          <Link to="/create">Create Campaign</Link>
          <span className="account-info">
            {account ? `Connected: ${account.slice(0, 6)}...${account.slice(-4)}` : 'Not Connected'}
          </span>
        </div>
      </div>
    </motion.nav>
  );

  if (loading) {
    return (
      <div className="loading">
        <h2>Loading Application</h2>
        <p>{networkStatus}</p>
        {error && <p className="error">{error}</p>}
      </div>
    );
  }

  return (
    <div className="App">
      <Navbar />
      <AnimatePresence mode="wait">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route
            path="/campaigns"
            element={
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.3 }}
              >
                <section className="campaigns-section">
                  <h2>Active Campaigns</h2>
                  <div className="campaigns-grid">
                    {campaigns.length === 0 ? (
                      <p>No campaigns found. Create one to get started!</p>
                    ) : (
                      campaigns.map((campaign) => (
                        <motion.div
                          key={campaign.id}
                          className="campaign-card"
                          initial={{ opacity: 0, y: 20 }}
                          animate={{ opacity: 1, y: 0 }}
                          transition={{ duration: 0.3 }}
                          whileHover={{ y: -5 }}
                        >
                          <h3>{campaign.title}</h3>
                          <p>{campaign.description}</p>
                          <p><strong>Beneficiary:</strong> {formatAddress(campaign.beneficiary)}</p>
                          <p><strong>Goal:</strong> {campaign.goal} ETH</p>
                          <p><strong>Raised:</strong> {campaign.raised} ETH</p>
                          <p><strong>Deadline:</strong> {formatDate(campaign.deadline)}</p>
                          <span className={`status-badge ${campaign.active ? 'status-active' : 'status-completed'}`}>
                            {campaign.active ? 'Active' : 'Completed'}
                          </span>
                          {campaign.active && (
                            <div>
                              <input 
                                type="number" 
                                placeholder="Amount (ETH)" 
                                id={`donation-${campaign.id}`}
                                step="0.01"
                                min="0.01"
                              />
                              <button onClick={() => donate(campaign.id, document.getElementById(`donation-${campaign.id}`).value)}>
                                Donate
                              </button>
                            </div>
                          )}
                        </motion.div>
                      ))
                    )}
                  </div>
                </section>
              </motion.div>
            }
          />
          <Route
            path="/create"
            element={
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.3 }}
              >
                <section>
                  <h2>Create New Campaign</h2>
                  <form onSubmit={createCampaign}>
                    <input 
                      type="text" 
                      name="title" 
                      placeholder="Campaign Title" 
                      required 
                    />
                    <textarea 
                      name="description" 
                      placeholder="Campaign Description" 
                      required 
                    />
                    <input 
                      type="text" 
                      name="beneficiary" 
                      placeholder="Beneficiary Address" 
                      required 
                    />
                    <input 
                      type="number" 
                      name="goal" 
                      placeholder="Goal Amount (ETH)" 
                      step="0.01" 
                      min="0.01" 
                      required 
                    />
                    <input 
                      type="number" 
                      name="duration" 
                      placeholder="Duration (days)" 
                      min="1" 
                      required 
                    />
                    <button type="submit">Create Campaign</button>
                  </form>
                </section>
              </motion.div>
            }
          />
        </Routes>
      </AnimatePresence>
    </div>
  );
}

export default App;
