import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const Home = () => {
  return (
    <div className="home">
      <section className="hero">
        <div className="container">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="hero-content"
          >
            <motion.img
              src="/images/charity-logo.png"
              alt="CharityChain Logo"
              className="hero-logo"
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ delay: 0.2, type: "spring", stiffness: 200 }}
            />
            <h1>Welcome to CharityChain</h1>
            <p>Transparent, secure, and impactful charitable giving powered by blockchain technology</p>
            <div className="hero-buttons">
              <Link to="/campaigns">
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  className="button-primary"
                >
                  <i className="fas fa-hands-helping"></i> View Campaigns
                </motion.button>
              </Link>
              <Link to="/create">
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  className="button-secondary"
                >
                  <i className="fas fa-plus-circle"></i> Create Campaign
                </motion.button>
              </Link>
            </div>
          </motion.div>
          <motion.div
            className="hero-image"
            initial={{ opacity: 0, x: 100 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.4, duration: 0.8 }}
          >
            <img src="/images/hero-illustration.svg" alt="Charity Illustration" />
          </motion.div>
        </div>
      </section>

      <section className="features">
        <div className="container">
          <motion.h2
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.2 }}
          >
            Why Choose CharityChain?
          </motion.h2>
          <div className="features-grid">
            <motion.div
              className="feature-card"
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: 0.3 }}
              whileHover={{ y: -5 }}
            >
              <div className="feature-icon">
                <i className="fas fa-shield-alt"></i>
              </div>
              <h3>Transparency</h3>
              <p>Every transaction is recorded on the blockchain, ensuring complete transparency in fund allocation.</p>
            </motion.div>
            <motion.div
              className="feature-card"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.4 }}
              whileHover={{ y: -5 }}
            >
              <div className="feature-icon">
                <i className="fas fa-lock"></i>
              </div>
              <h3>Security</h3>
              <p>Smart contracts ensure that funds are only released when campaign goals are met.</p>
            </motion.div>
            <motion.div
              className="feature-card"
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: 0.5 }}
              whileHover={{ y: -5 }}
            >
              <div className="feature-icon">
                <i className="fas fa-chart-line"></i>
              </div>
              <h3>Impact</h3>
              <p>Track the real impact of your donations with verifiable results on the blockchain.</p>
            </motion.div>
          </div>
        </div>
      </section>

      <section className="how-it-works">
        <div className="container">
          <motion.h2
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.6 }}
          >
            How It Works
          </motion.h2>
          <div className="steps-grid">
            <motion.div
              className="step-card"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.7 }}
              whileHover={{ y: -5 }}
            >
              <div className="step-number">1</div>
              <div className="step-icon">
                <i className="fas fa-wallet"></i>
              </div>
              <h3>Connect Your Wallet</h3>
              <p>Connect your MetaMask wallet to get started with secure transactions.</p>
            </motion.div>
            <motion.div
              className="step-card"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.8 }}
              whileHover={{ y: -5 }}
            >
              <div className="step-number">2</div>
              <div className="step-icon">
                <i className="fas fa-search"></i>
              </div>
              <h3>Choose a Campaign</h3>
              <p>Browse through active campaigns and select one that resonates with you.</p>
            </motion.div>
            <motion.div
              className="step-card"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.9 }}
              whileHover={{ y: -5 }}
            >
              <div className="step-number">3</div>
              <div className="step-icon">
                <i className="fas fa-hand-holding-heart"></i>
              </div>
              <h3>Make a Donation</h3>
              <p>Contribute ETH to support causes you believe in, with full transparency.</p>
            </motion.div>
          </div>
        </div>
      </section>

      <section className="stats-section">
        <div className="container">
          <div className="stats-grid">
            <motion.div
              className="stat-card"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ delay: 0.2 }}
            >
              <i className="fas fa-users"></i>
              <h3>1,000+</h3>
              <p>Active Donors</p>
            </motion.div>
            <motion.div
              className="stat-card"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ delay: 0.3 }}
            >
              <i className="fas fa-project-diagram"></i>
              <h3>500+</h3>
              <p>Campaigns</p>
            </motion.div>
            <motion.div
              className="stat-card"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ delay: 0.4 }}
            >
              <i className="fas fa-coins"></i>
              <h3>1000 ETH</h3>
              <p>Total Donations</p>
            </motion.div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
